function log(message, type = 'info') {
  const prefix = {
    info: 'üìã',
    success: '‚úÖ',
    warning: '‚ö†Ô∏è',
    error: '‚ùå',
    header: '‚ö°',
    git: 'üêô',
    quantum: 'üåü',
    smart: 'üß†'
  };

  const prefixText = prefix[type];

  switch (type) {
    case 'header':
      console.log(chalk.magenta.bold(`${prefixText} ${message}`));
      break;
    case 'success':
      console.log(chalk.green(`${prefixText} ${message}`));
      break;
    case 'warning':
      console.log(chalk.yellow(`${prefixText} ${message}`));
      break;
    case 'error':
      console.log(chalk.red(`${prefixText} ${message}`));
      break;
    case 'git':
      console.log(chalk.blue(`${prefixText} ${message}`));
      break;
    case 'quantum':
      console.log(chalk.magenta(`${prefixText} ${message}`));
      break;
    case 'smart':
      console.log(chalk.cyan(`${prefixText} ${message}`));
      break;
    default:
      console.log(chalk.blue(`${prefixText} ${message}`));
  }
}

function execCommand(command, description, options = {}) {
  try {
    log(`Executing: ${description}`, 'git');
    const result = execSync(command, { 
      encoding: 'utf8', 
      stdio: 'pipe',
      timeout: 30000,
      ...options
    });
    log(`Success: ${description}`, 'success');
    return { success: true, output: result, cmd: command, description };
  } catch (error) {
    log(`Error: ${description} - ${error.message}`, 'error');
    return { success: false, error: error.message, cmd: command, description };
  }
}

function getGitStatus() {
  try {
    const status = execSync('git status --porcelain', { encoding: 'utf8' });
    return status.trim().split('\n').filter(line => line.length > 0);
  } catch (error) {
    return [];
  }
}

function getStagedFiles() {
  try {
    const staged = execSync('git diff --cached --name-only', { encoding: 'utf8' });
    return staged.trim().split('\n').filter(line => line.length > 0);
  } catch (error) {
    return [];
  }
}

function getChangedFiles() {
  try {
    const changed = execSync('git diff --name-only', { encoding: 'utf8' });
    return changed.trim().split('\n').filter(line => line.length > 0);
  } catch (error) {
    return [];
  }
}

function getCurrentBranch() {
  try {
    const branch = execSync('git branch --show-current', { encoding: 'utf8' });
    return branch.trim();
  } catch (error) {
    return 'main';
  }
}

function generateSmartCommitMessage() {
  const changedFiles = getStagedFiles();
  const unstagedFiles = getChangedFiles();
  const allFiles = [...new Set([...changedFiles, ...unstagedFiles])];

  // Analyze file types and changes
  const fileTypes = {
    config: ['.json', '.js', '.ts', '.yml', '.yaml'],
    code: ['.tsx', '.jsx', '.ts', '.js'],
    styles: ['.css', '.scss', '.sass'],
    docs: ['.md', '.txt'],
    db: ['.sql', '.prisma'],
    test: ['.test.', '.spec.']
  };

  const changes = {
    config: 0,
    code: 0,
    styles: 0,
    docs: 0,
    db: 0,
    test: 0,
    other: 0
  };

  allFiles.forEach(file => {
    let categorized = false;
    for (const [type, extensions] of Object.entries(fileTypes)) {
      if (extensions.some(ext => file.includes(ext))) {
        changes[type]++;
        categorized = true;
        break;
      }
    }
    if (!categorized) changes.other++;
  });

  // Generate smart commit message
  const timestamp = new Date().toISOString().split('T')[0];
  const branch = getCurrentBranch();

  let message = '';
  const changeTypes = Object.entries(changes)
    .filter(([_, count]) => count > 0)
    .map(([type, count]) => ({ type, count }));

  if (changeTypes.length === 0) {
    message = `üîß Maintenance and cleanup - ${timestamp}`;
  } else if (changeTypes.length === 1) {
    const [{ type, count }] = changeTypes;
    const typeMessages = {
      config: `‚öôÔ∏è Configuration updated`,
      code: `üíª Code improvements`,
      styles: `üé® Styling updates`,
      docs: `üìö Documentation updated`,
      db: `üóÑÔ∏è Database changes`,
      test: `üß™ Test updates`,
      other: `üîß General updates`
    };
    message = `${typeMessages[type]} - ${timestamp}`;
  } else {
    message = `üöÄ Multi-component updates - ${timestamp}`;
  }

  // Add branch context if not main
  if (branch !== 'main') {
    message += ` [${branch}]`;
  }

  // Add quantum enhancement
  message += `\n\n‚ö° Quantum-enhanced commit - AETHERIUS-PRIME intelligence`;
  message += `\nüß† Smart analysis: ${changeTypes.map(t => `${t.count} ${t.type}`).join(', ')}`;
  message += `\nüîí Encryption: ${GIT_CONFIG.quantum.encryption}`;
  message += `\nüåü Coherence: ${GIT_CONFIG.quantum.coherence}`;

  return message;
}

// ‚ö° SMART GIT COMMANDS CLASS
class KeystoneSmartGit {
  constructor() {
    this.config = GIT_CONFIG;
  }

  // üé® UTILITY FUNCTIONS (moved inside class)
  log(message, type = 'info') {
    const prefix = {
      info: 'üìã',
      success: '‚úÖ',
      warning: '‚ö†Ô∏è',
      error: '‚ùå',
      header: '‚ö°',
      git: 'üêô',
      quantum: 'üåü',
      smart: 'üß†'
    };

    const prefixText = prefix[type];

    switch (type) {
      case 'header':
        console.log(chalk.magenta.bold(`${prefixText} ${message}`));
        break;
      case 'success':
        console.log(chalk.green(`${prefixText} ${message}`));
        break;
      case 'warning':
        console.log(chalk.yellow(`${prefixText} ${message}`));
        break;
      case 'error':
        console.log(chalk.red(`${prefixText} ${message}`));
        break;
      case 'git':
        console.log(chalk.blue(`${prefixText} ${message}`));
        break;
      case 'quantum':
        console.log(chalk.magenta(`${prefixText} ${message}`));
        break;
      case 'smart':
        console.log(chalk.cyan(`${prefixText} ${message}`));
        break;
      default:
        console.log(chalk.blue(`${prefixText} ${message}`));
    }
  }

  execCommand(command, description, options = {}) {
    try {
      this.log(`Executing: ${description}`, 'git');
      const result = execSync(command, { 
        encoding: 'utf8', 
        stdio: 'pipe',
        timeout: 30000,
        ...options
      });
      this.log(`Success: ${description}`, 'success');
      return { success: true, output: result, cmd: command, description };
    } catch (error) {
      this.log(`Error: ${description} - ${error.message}`, 'error');
      return { success: false, error: error.message, cmd: command, description };
    }
  }

  async smartAdd(options = {}) {
    this.log('‚ö° SMART ADD - Intelligent File Staging', 'header');

    const allFiles = options.all || false;
    const specific = options.files || [];

    if (specific.length > 0) {
      // Add specific files
      for (const file of specific) {
        const result = this.execCommand(`git add ${file}`, `Add ${file}`);
        if (!result.success) {
          this.log(`Failed to add ${file}`, 'error');
        }
      }
    } else if (allFiles) {
      // Add all files
      const result = this.execCommand('git add .', 'Add all files');
      if (!result.success) {
        this.log('Failed to add all files', 'error');
        return false;
      }
    } else {
      // Smart add - analyze changes
      const status = getGitStatus();
      if (status.length === 0) {
        this.log('No changes to stage', 'warning');
        return true;
      }

      // Add modified and new files
      const modified = status.filter(line => line.startsWith(' M') || line.startsWith('??'));
      for (const file of modified) {
        const fileName = file.substring(3);
        const result = this.execCommand(`git add ${fileName}`, `Add ${fileName}`);
        if (!result.success) {
          this.log(`Failed to add ${fileName}`, 'error');
        }
      }
    }

    this.log('‚úÖ Smart add completed', 'success');
    return true;
  }

  async smartCommit(options = {}) {
    this.log('‚ö° SMART COMMIT - Intelligent Message Generation', 'header');

    const message = options.message || generateSmartCommitMessage();
    const amend = options.amend || false;

    // Check if there are staged changes
    const staged = getStagedFiles();
    if (staged.length === 0 && !amend) {
      this.log('No staged changes to commit', 'warning');
      return false;
    }

    const commitCmd = amend 
      ? `git commit --amend -m "${message}"`
      : `git commit -m "${message}"`;

    const result = this.execCommand(commitCmd, 'Smart commit');
    if (!result.success) {
      this.log('Smart commit failed', 'error');
      return false;
    }

    this.log('‚úÖ Smart commit completed', 'success');
    return true;
  }

  async smartPush(options = {}) {
    this.log('‚ö° SMART PUSH - Intelligent Repository Updates', 'header');

    const branch = options.branch || getCurrentBranch();
    const force = options.force || false;
    const tags = options.tags || false;

    // Push to remote
    const pushCmd = force 
      ? `git push origin ${branch} --force`
      : `git push origin ${branch}`;

    const result = this.execCommand(pushCmd, `Push to ${branch}`);
    if (!result.success) {
      this.log('Smart push failed', 'error');
      return false;
    }

    // Push tags if requested
    if (tags) {
      const tagResult = this.execCommand('git push origin --tags', 'Push tags');
      if (!tagResult.success) {
        this.log('Tag push failed', 'warning');
      }
    }

    this.log('‚úÖ Smart push completed', 'success');
    return true;
  }

  async smartPull(options = {}) {
    this.log('‚ö° SMART PULL - Intelligent Repository Updates', 'header');

    const branch = options.branch || getCurrentBranch();
    const rebase = options.rebase || false;

    const pullCmd = rebase
      ? `git pull origin ${branch} --rebase`
      : `git pull origin ${branch}`;

    const result = this.execCommand(pullCmd, `Pull from ${branch}`);
    if (!result.success) {
      this.log('Smart pull failed', 'error');
      return false;
    }

    this.log('‚úÖ Smart pull completed', 'success');
    return true;
  }

  async smartStatus() {
    this.log('‚ö° SMART STATUS - Intelligent Repository Analysis', 'header');

    // Get comprehensive status
    const status = this.execCommand('git status', 'Repository status');
    const branch = this.execCommand('git branch --show-current', 'Current branch');
    const remote = this.execCommand('git remote -v', 'Remote repositories');
    const log = this.execCommand('git log --oneline -5', 'Recent commits');

    // Analyze changes
    const changedFiles = getChangedFiles();
    const stagedFiles = getStagedFiles();
    const untrackedFiles = getGitStatus()
      .filter(line => line.startsWith('??'))
      .map(line => line.substring(3));

    this.log(`üìä Repository Analysis:`, 'smart');
    this.log(`  Branch: ${branch.output.trim()}`, 'info');
    this.log(`  Changed files: ${changedFiles.length}`, 'info');
    this.log(`  Staged files: ${stagedFiles.length}`, 'info');
    this.log(`  Untracked files: ${untrackedFiles.length}`, 'info');

    if (changedFiles.length > 0) {
      this.log(`üìù Modified files:`, 'info');
      changedFiles.forEach(file => this.log(`  ‚Ä¢ ${file}`, 'info'));
    }

    if (untrackedFiles.length > 0) {
      this.log(`üÜï Untracked files:`, 'info');
      untrackedFiles.forEach(file => this.log(`  ‚Ä¢ ${file}`, 'info'));
    }

    this.log('‚úÖ Smart status completed', 'success');
    return true;
  }

  async smartSync(options = {}) {
    this.log('‚ö° SMART SYNC - Complete Repository Synchronization', 'header');

    const success = await this.smartPull(options);
    if (!success) {
      this.log('Smart sync failed during pull', 'error');
      return false;
    }

    const pushSuccess = await this.smartPush(options);
    if (!pushSuccess) {
      this.log('Smart sync failed during push', 'error');
      return false;
    }

    this.log('‚úÖ Smart sync completed', 'success');
    return true;
  }

  async quickCommit(options = {}) {
    this.log('‚ö° QUICK COMMIT - One-Shot Complete Operation', 'header');

    // Add all changes
    const addSuccess = await this.smartAdd({ all: true });
    if (!addSuccess) {
      this.log('Quick commit failed during add', 'error');
      return false;
    }

    // Smart commit
    const commitSuccess = await this.smartCommit(options);
    if (!commitSuccess) {
      this.log('Quick commit failed during commit', 'error');
      return false;
    }

    // Smart push
    const pushSuccess = await this.smartPush(options);
    if (!pushSuccess) {
      this.log('Quick commit failed during push', 'error');
      return false;
    }

    this.log('‚úÖ Quick commit completed - All operations successful', 'success');
    return true;
  }

  async smartTag(options = {}) {
    this.log('‚ö° SMART TAG - Intelligent Tag Management', 'header');

    const version = options.version || `v${Date.now()}`;
    const message = options.message || `Release ${version}`;
    const push = options.push !== false;

    // Create tag
    const tagCmd = `git tag -a ${version} -m "${message}"`;
    const result = this.execCommand(tagCmd, `Create tag ${version}`);
    if (!result.success) {
      this.log('Smart tag failed', 'error');
      return false;
    }

    // Push tag if requested
    if (push) {
      const pushResult = this.execCommand(`git push origin ${version}`, `Push tag ${version}`);
      if (!pushResult.success) {
        this.log('Tag push failed', 'warning');
      }
    }

    this.log(`‚úÖ Smart tag completed: ${version}`, 'success');
    return true;
  }
}

